sys_variable_value.value:
(function execute(inputs, outputs) {
  inputs = new JiraUtils().trimStringInputs(inputs);
  outputs.issue_id = inputs.issue_id;
  outputs.api_version = new JiraUtils().getAPIVersion(inputs.sys_alias.sys_id);
})(inputs, outputs);

