sys_variable_value.value:
(function execute(inputs, outputs) {
  //set an error
  if (inputs.status_code != '200') {
    outputs.status = "Error"
    outputs.error_message = new JiraErrorHandler().setError(inputs.status_code, inputs.response);
  } else {
    outputs.status = "Success"
    outputs.jira_transitions = inputs.response;
  }
})(inputs, outputs);

