sys_variable_value.value:
(function execute(inputs, outputs) {
    if(inputs.status == "200") {
        var payload = JSON.parse(inputs.response);
       if (payload.size > 0) {

         outputs.organizations= payload.values;

            outputs.status = "Success";
        } else {
            outputs.status = "Error";
            outputs.error = payload.errorMessage;
        }
    }else {
            outputs.status = "Error";
            outputs.error = "Invalid Service Desk ID.";
    }
})(inputs, outputs);

