sys_variable_value.value:
(function execute(inputs, outputs) {
    if(inputs.status == "200") {
        var payload = JSON.parse(inputs.response);
      gs.info("payload : " + payload.toString());
      gs.info("inputs.response.size : " + payload.size);
        if (payload.size > 0) {
            var organizations = payload.values.map(function (organization) {
                    gs.info("organization : " + organization.toString());

                return {
                    id: organization.id,
                    name: organization.name
                };
            });
            outputs.organizations = {
                data: organizations
            };
            outputs.status = "Success";
        } else {
            outputs.status = "Error";
            outputs.error = "Invalid Project ID.";
        }
    }
})(inputs, outputs);

