sys_variable_value.value:
(function execute(inputs, outputs) {
    var responseBody = JSON.parse(inputs.response);
  if(inputs.status_code == "200") {
    outputs.status = "Success";
      var groups= responseBody.groups;
    var groups_object= { 
   			 data : []
    					};


    if (inputs.status == '201') {
        outputs.status = "Success";
        outputs.organization_id = response.id;
        outputs.name = response.name;
    } 


    var responseBody = JSON.parse(inputs.response);
  if(inputs.status_code == "200") {
    outputs.status = "Success";
    var groups= responseBody.groups;
    var groups_object= { 
   			 data : []
    					};

    groups.map(function(group, index) {
      	groups_object.data.push({
        		name : group.groupId,
          		label : group.groupName
        })
    	gs.info("group id at index "+index+" is = "+group.groupId);
      	if(group.groupName == inputs.group_name)
        {
          outputs.group_id=group.groupId;
        }
    });
    outputs.groups= groups_object;
  } else {
        outputs.error_message = inputs.error_message;
  	    outputs.status = "Error";
    }
})(inputs, outputs);

