sys_variable_value.value:
(function execute(inputs, outputs) {
//  gs.info(" response : "+ inputs.response.toString());


  if(inputs.status == '204'){
   outputs.status = "Success";

  }else
  {
   // var payload = JSON.parse(inputs.response);
   // outputs.error = payload.errorMessage;
   outputs.status = "Error";

  }

})(inputs, outputs);

