sys_variable_value.value:
(function paginate(variables, pageResponse) {
      variables.getNextPage = ((variables.isLastPage) && (variables.isLastPage == "true")) ? false : true;
        var start, urlStart;
    if(variables.nextLink){
      urlStart = ((variables.nextLink).split("start="))[1];
      variables.response = (urlStart.split("&"))[0];
    }
})(variables, pageResponse);

