sys_variable_value.value:
(function execute(inputs, outputs) {
    var response = JSON.parse(inputs.response);
    if (inputs.status == '200') {
        if (response.size > 0) {
            var values = response.values.map(function (value) {
                return {
                    id: value.id,
                    name: value.name
                };
            });
            outputs.org_values = JSON.stringify(values);
        }
        gs.info(" Response : " + response.toString() + " AND ID = : " + response.id + " and Name : " + response.name);
        outputs.status = "Success";
    } else {
        outputs.error_message = inputs.error_message;
        outputs.status = "Error";

    }



    //  {
    //  "id": "1",
    //  "name": "Charlie Cakes Franchises",
    //  "_links": {
    //    "self": "https://your-domain.atlassian.net/rest/servicedeskapi/organization/1"
    //  }

    // ... code ...
})(inputs, outputs);

