sys_variable_value.value:
(function execute(inputs, outputs) {
    var data = new Array();
    var service_desk;
    if (inputs.status == '200') {
        var response_json = JSON.parse(inputs.response);
        for (var i = 0; i < response_json.size; i++) {
            service_desk = {};
            service_desk.label = response_json.values[i].projectKey;
            service_desk.name = response_json.values[i].id;
            data.push(service_desk);
        }

        var service_desks = {};
        service_desks.data = data;
        outputs.service_desk_ids = service_desks;
        outputs.status = "Success";
    }
    else {
        try {
            outputs.error = payload.errorMessage;
        } catch (error) {
        }
        outputs.status = "Error";
    }

})(inputs, outputs);

