sys_variable_value.value:
(function execute(inputs, outputs) {
    var responseBody = JSON.parse(inputs.response);
    if (inputs.status_code == "200") {
        outputs.status = "Success";
        var requestTypeFields = responseBody.requestTypeFields;
        var request_type_fields_object = {
            data: []
        };
        requestTypeFields.map(function (requestTypeField) {
            request_type_fields_object.data.push({
                fieldId: requestTypeField.fieldId,
                name: requestTypeField.name,
                required: requestTypeField.required
            })
        });
        outputs.request_type_fields = JSON.stringify(request_type_fields_object);
    } else {
        if (inputs.status_code == "404") {
            outputs.error_message = "The service desk or request type do not exist.";
        }
        else {
            outputs.error_message = inputs.error_message;
        }
        outputs.status = "Error";
    }
})(inputs, outputs);

