sys_variable_value.value:
(function execute(inputs, outputs) {
    if (((inputs.issue_id).trim() == "") && ((inputs.key).tr == "")) {
        throw new Error("One of Issue Id and key is mandatory");
    } else {
        if ((inputs.issue_id).trim() != "") {
            outputs.issue_id = (inputs.issue_id).trim();
        } else {
            outputs.issue_id = (inputs.key).trim();
        }
        outputs.id = inputs.id;
        if ((inputs.additional_comments).trim() != "") {
            var payload;
            payload = "\",additionalComment\":{\"body\" : \" " + (inputs.additional_comments).trim() + "\"}";
            outputs.payload = payload;
        }
    }
})(inputs, outputs);

