sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.issue_id = inputs.issue_id;
  outputs.temporary_attachment_id = JSON.stringify(inputs.temporary_attachment_id);
  outputs.comment=inputs.comment.trim();
  outputs.public  = inputs.public;
})(inputs, outputs);

