sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.status_code == 201) {
        outputs.status = "Success";
      outputs.response = JSON.parse(inputs.response);

    } else {
        outputs.status = "Error";
        outputs.error_message = inputs.error_message;
    }
})(inputs, outputs);

